	if focus ==hotspots then
   -- basic arrow input for hotspot selection.
   		cycle(hotspots)
   
   -- X: cancel hotspot verb
   		if btnp(5) then 
   				focus=verbs; inform(nil); while #hotspots>0 do rem(hotspots) end; hotspots.i=1 
